mapper.insert:
INSERT	INTO MEMBER (
	ID
	, NAME
	, PASSWORD
	, NICKNAME
	, PHONENUM
	, ADDR
)
VALUES (
	MEMBER_ID_SEQ.NEXTVAL
	, #{name}
	, #{password}
	, #{nickname}
	, #{phoneNum}
	, #{addr}
)

mapper.update:
UPDATE	MEMBER
SET		
		NAME= #{name}


		,PASSWORD= #{password}


		,NICKNAME= #{nickname}


		,PHONENUM= #{phoneNum}


		,ADDR= #{addr}

WHERE	ID= #{id}
