mapper.insert:
INSERT	INTO MEMBER (
	ID
	, EMAIL
	, PASSWORD
	, NICKNAME
	, PHONE_NUM
	, ADDR
	, SALT
)
VALUES (
	MEMBER_ID_SEQ.NEXTVAL
	, #{email}
	, #{password}
	, #{nickname}
	, #{phoneNum}
	, #{addr}
	, #{salt}
)

mapper.update:
UPDATE	MEMBER
SET		
		EMAIL= #{email}


		,PASSWORD= #{password}
		,SALT= #{salt}


		,NICKNAME= #{nickname}


		,PHONE_NUM= #{phoneNum}


		,ADDR= #{addr}

WHERE	ID= #{id}

mapper.select:
SELECT	NVL(M.SALT, '')
FROM 	MEMBER M
WHERE	M.EMAIL = #{email}

SELECT	COUNT(ID)
FROM	MEMBER
WHERE	EMAIL= #{email}

SELECT	COUNT(ID)
FROM	MEMBER
WHERE	NICKNAME= #{nickname}
